import "@copilotkit/react-ui/styles.css";
import React, { ReactNode } from "react";
import { CopilotKit } from "@copilotkit/react-core";

// Where CopilotKit will proxy requests to. If you're using Copilot Cloud, this environment variable will be empty.
const runtimeUrl = process.env.NEXT_PUBLIC_COPILOTKIT_RUNTIME_URL
// When using Copilot Cloud, all we need is the publicApiKey.
const publicApiKey = process.env.NEXT_PUBLIC_COPILOT_API_KEY;
// The name of the agent that we'll be using.
const agentName = process.env.NEXT_PUBLIC_COPILOTKIT_AGENT_NAME

export default function Layout({ children }: { children: ReactNode }) {
  return (
    <CopilotKit 
      runtimeUrl={runtimeUrl}
      publicApiKey={publicApiKey}
      agent={agentName}
    >
      {children}
    </CopilotKit>
  );
}

import { NextRequest } from "next/server";
import {
  CopilotRuntime,
  copilotRuntimeNextJSAppRouterEndpoint,
  ExperimentalEmptyAdapter,
  LangGraphAgent,
} from "@copilotkit/runtime";

const serviceAdapter = new ExperimentalEmptyAdapter();

const agentName = process.env.NEXT_PUBLIC_COPILOTKIT_AGENT_NAME;
if (!agentName) {
  throw new Error(
    "Missing env NEXT_PUBLIC_COPILOTKIT_AGENT_NAME – required for LangGraph agent registration"
  );
}

if (!process.env.LANGGRAPH_DEPLOYMENT_URL) {
  throw new Error(
      "Missing env LANGGRAPH_DEPLOYMENT_URL – required for to know where to read the LangGraph agent from"
  );
}

const runtime = new CopilotRuntime({
  agents: {
    [agentName]: new LangGraphAgent({
      deploymentUrl: process.env.LANGGRAPH_DEPLOYMENT_URL,
      langsmithApiKey: process.env.LANGSMITH_API_KEY || "", // only used in LangGraph Platform deployments
      graphId: agentName
    })
  }
});

export const POST = async (req: NextRequest) => {
  const { handleRequest } = copilotRuntimeNextJSAppRouterEndpoint({
    runtime,
    serviceAdapter,
    endpoint: "/api/copilotkit",
  });

  return handleRequest(req);
};
